## WEEKTAAK 3
while True:
    data = input("Wat is de sequentie ")
    alles = len(data)
    a = data.count("A")
    t = data.count("T")
    g = data.count("G")
    c = data.count("C")
    n = data.count("N")
    atgcn = a+t+g+c+n #Telt alle basen bij elkaar op

    if atgcn == alles: #Als het er in de basen geen andere tekens zitten dan ATGCN dan zegt hij dat het DNA is
        print("Het is RNA")
        print("Totaal A ",a)
        print("Totaal T ",t)
        print("Totaal G ",g)
        print("Totaal C ",c)
        print("Totaal N ",n)
        print ("Totaal van de sequentie ",alles)
    else: #Anders is het een eiwit
        print("Het is een eiwit sequentie")
        d = data.count("D")
        e = data.count("E")
        r = data.count("R")
        k = data.count("K")
        print("Totaal D ",d)
        print("Totaal E ",e)
        print("Totaal R ",r)
        print("Totaal K ",k)
        print ("Totaal van de sequentie ",alles)

        if r+k > d+e: #Als de R en K bij elkaar opgeteld groter zijn dan de D en E bij elkaar opgeteld is het positief
            geladen = "postitief"
        elif r+k < d+e: #Als de R en K bij elkaar opgeteld kleiner zijn dan de D en E bij elkaar opgeteld is het negatief
            geladen = "negatief"
        else: #Anders is het neutraal
            geladen = "neutraal"
        print("Dit eiwit is bij een Ph van 7 ",geladen," geladen"+"\n")
        
    print("Voor het vergelijken van de eiwit sequentie en de RNA sequentie vul dan onderstaande iputs in")
    eiwit = input("\n"+"Wat is de eiwit sequentie ")
    DNA = input("\n"+"wat is de DNA sequentie ")

    lengte_dna = len(DNA)
    lengte_eiwit = len(eiwit)

    print ("\n"+"Het DNA is "+str(lengte_dna/lengte_eiwit)+" groter") # berekent de hoeveel keer groter het stuk eiwit is ten opzichte van het DNA
    print (" percetage van DE ",(d+e)/alles*100, "het percetage van RK is ",(r+k)/alles*100, "en het percetage van DERK is",(d+e+r+k)/alles*100)

    while True:
        answer = input('Run again? (y/n): ')
        if answer in ('y', 'n'):
            break
        print ('Invalid input.')
    if answer == 'y':
        continue
    else:
        print ('Goodbye')
        break

