d = 115.0886
c = 103.1388
e = 129.1155
q = 128.1307
g = 57.0519
h = 137.1411
i = 113.1594
l = 113.1594
k = 128.1741
m = 131.1926
f = 147.1766
p = 97.1167
s = 87.0782
t = 101.1051
w = 186.2132
y = 163.1760
v = 99.1326

#invullen van de sequentie
eiwit = input("Wat is de eiwitsequentie? ")

#telt de aminozuren 
A = eiwit.count("A")
R = eiwit.count("R")
N = eiwit.count("N")
D = eiwit.count("D")
C = eiwit.count("C")
E = eiwit.count("E")
Q = eiwit.count("Q")
G = eiwit.count("G")
H = eiwit.count("H")
I = eiwit.count("I")
L = eiwit.count("L")
K = eiwit.count("K")
M = eiwit.count("M")
F = eiwit.count("F")
P = eiwit.count("P")
S = eiwit.count("S")
T = eiwit.count("T")
W = eiwit.count("W")
Y = eiwit.count("Y")
V = eiwit.count("V")

#berekent de totale massa van de eiwitsequentie
massa = (A*a)+(R*r)+(N*n)+(D*d)+(C*c)+(E*e)+(Q*q)+(G*g)+(H*h)+(I*i)+(L*l)+(K*k)+(M*m)+(F*f)+(P*p)+(S*s)+(T*t)+(W*w)+(Y*y)+(V*v)

print("De massa van het eiwit is", massa, "Da.")

## WEEKTAAK 5
#Auteur: Teun van Dorp
#Datum: 2/10/2019
#berekent hoeveel van de aminozuren hetzelde zijn

#input van de eiwitsequenties
eiwit1 = input("Wat is de eerste eiwitsequentie? ")
eiwit2 = input("Wat is de tweede eiwitsequentie? ")

#lengte van de eiwitsequenties
len1 = len(eiwit1)
len2 = len(eiwit2)
tel = 0

#lengte van eiwitsequenties printen
print("Eiwit 1 is", len1, "lang", "\n" + "Eiwit 2 is", len2, "lang")

#kijken welke sequentie langer is en vervolgens in lenlang/lenklein zetten
if len1 < len2:
    lenlang = len2
    lenklein = len1
else:
    lenlang = len1
    lenklein = len2

#kijken op welke locaties de letters hetzelfde zijn en hoevaak dat is
for plaats in range(lenklein):
    if eiwit1[plaats] == eiwit2[plaats]:
        tel += 1

#print het percentage dat hetzelde is
print("Aantal gelijke letters:", tel)
print("De sequenties zijn voor", tel/lenlang*100, "% gelijk.")
